# Step 1: Search for Beatles-themed attractions in Liverpool
search_attraction_query = "Liverpool Beatles"
search_attraction_params = {
    "query": search_attraction_query
}
attraction_search_result = F.Search_Attraction_Location(**search_attraction_params)

# Error handling
if not attraction_search_result.get('status'):
    raise Exception(f"Search_Attraction_Location failed: {attraction_search_result.get('message', 'Unknown error')}")

# Extract product slugs
product_slugs = [product['productSlug'] for product in attraction_search_result.get('products', [])]

# Step 2: Get details for each attraction and find one that costs less than 200
for slug in product_slugs:
    get_attraction_details_params = {
        "slug": slug,
        "currency_code": "USD"
    }
    attraction_details_result = F.Get_Attraction_Details(**get_attraction_details_params)

    # Error handling
    if not attraction_details_result.get('status'):
        raise Exception(f"Get_Attraction_Details failed: {attraction_details_result.get('message', 'Unknown error')}")

    # Extract representative price
    representative_price = attraction_details_result.get('representativePrice', {}).get('chargeAmount', 0)
    attraction_name = attraction_details_result.get('name', 'Unknown Attraction')

    # Check if the price is less than 200
    if representative_price < 200:
        print(f"Found a Beatles-themed attraction in Liverpool: {attraction_name} with a cost of ${representative_price}")
        break
else:
    print("No Beatles-themed attractions found with a cost less than $200.")
