    raise Exception(f"Location_to_Lat_Long failed: {london_lat_long_result.get('message', 'Unknown error')}")

london_latitude = london_lat_long_result['data'][0]['geometry']['location']['lat']
london_longitude = london_lat_long_result['data'][0]['geometry']['location']['lng']

# Step 4: Get nearby cities to London
nearby_cities_params = {
    'latitude': str(london_latitude),
    'longitude': str(london_longitude)
}
